import { Component } from 'react';

import MarvelService from '../../services/MarvelService';
import Spinner from '../spinner/Spinner';
import ErrorMessage from '../errorMessage/ErrorMessage';

import './charList.scss';

class CharList extends Component {

    constructor (props) {
        super(props)

        this.state = {
            charArr: [],
            loading: true,
            error: false
        }
    }

    marvelService = new MarvelService();

    componentDidMount() {
        this.updateCharList();
    }

    onCharListLoaded = (charArr) => {
        this.setState({charArr, loading: false, error: false});
    }

    updateCharList = () => {
        this.marvelService
            .getAllCharacters()
            .then(charArr => this.onCharListLoaded(charArr))
            .catch(e => this.setState({loading: false, error: true}))
    }

    renderCharListItems = (charArr) => {
        const items = charArr.map(item => {
            const {name, thumbnail, thumbnailCoverStatus, id} = item;
            return (
                <li className="char__item" onClick={this.props.onCharSelected(id)} key={id}>
                    <img className={thumbnailCoverStatus} src={thumbnail} alt={name}/>
                    <div className="char__name">{name}</div>
                </li>
            )
        })

        return (
            <ul className="char__grid">
                {items}
            </ul>
        )
    }

    render = () => {
        const {charArr, loading, error} = this.state;
        let statusLoading = loading ? Spinner : null;
        let statusError = error ? ErrorMessage : null;
        let currentStatus = statusError || statusLoading || this.renderCharListItems

        return (
            <div className="char__list">
                {currentStatus(charArr)}
                <button className="button button__main button__long">
                    <div className="inner">load more</div>
                </button>
            </div>
        )
    }
}

export default CharList;

