import { useState } from "react";
import { useForm } from "react-hook-form"

import './searchForm.scss';

const SearchForm = () => {

    const {register, handleSubmit, formState: {errors}} = useForm();

    const onSubmit = data => console.log(data);

    return (
        <form onSubmit={handleSubmit(onSubmit)} className="char__search">
            <div className="char__search_label">Or find a character by name:</div>
            <input 
                {...register("name", {
                    required: 'This field is required',
                    // minLength: {
                    //     value: 2,
                    //     message: 'Enter full character name'
                    // }
                })} 
                // id="name" 
                // type="text" 
                className="char__search_input"
                // placeholder="Enter name"
            >
            {errors?.name?.type === 'required' ? <p className="char__search_error">{errors.name}</p> : null}
            </input>
            <button 
                type="submit" 
                className="char__search_button button button__main"
                onClick={() => console.log(errors.name)}
            >
                <div className="inner">find</div>
            </button>
        </form>
    )
}
export default SearchForm;
