import { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';

import useMarvelService from '../../services/MarvelService';
import Spinner from '../spinner/Spinner';
import ErrorMessage from '../errorMessage/ErrorMessage';

import './singleComicPage.scss';

const SingleComicPage = () => {

    const [comic, setComic] = useState({});
    const {loading, error, getComicById, clearError} = useMarvelService();
    const {comicId} = useParams();

    useEffect(() => {
        onComicLoad();
    }, [comicId]);

    const onComicLoad = () => {
        clearError();
        getComicById(comicId)
        .then(resp => setComic(resp))
        .catch(e => {});
    }

    const View = ({title, price, thumbnail, description, pageCount}) => {
        return (
            <>
                <img src={thumbnail} alt="x-men" className="single-comic__img"/>
                <div className="single-comic__info">
                    <h2 className="single-comic__name">{title}</h2>
                    <p className="single-comic__descr">{description}</p>
                    <p className="single-comic__descr">{pageCount} pages</p>
                    <p className="single-comic__descr">Language: en-us</p>
                    <div className="single-comic__price">{price}</div>
                </div>
                <Link to={'../comics'} className="single-comic__back">Back to all</Link>
            </>
        );
    }

    let statusLoading = loading ? Spinner : null;
    let statusError = error ? ErrorMessage : null;
    let currentStatus = statusError || statusLoading || View;

    return (
        <div className="single-comic">
            {currentStatus(comic)}
        </div>
    )
}

export default SingleComicPage;
