class MarvelService {
    _apiBase = 'https://gateway.marvel.com:443/v1/public/';
    _apiKey = 'apikey=bd35b0eefb6ce288f155f735ed56a8d6';

    getResource = async (url) => {
        let res = await fetch(url)

        if (!res.ok) {
            throw new Error(`Could not fetch ${url}, status: ${res.status}`)
        }

        return await res.json();
    }

    getAllCharacters = async () => {
        const res = await this.getResource(`${this._apiBase}characters?limit=9&offset=333&${this._apiKey}`);
        return res.data.results.map(this._transformCharacter);
    }

    getCharacterById = async (id) => {
        const res = await this.getResource(`${this._apiBase}characters/${id}?${this._apiKey}`);
        return this._transformCharacter(res.data.results[0]);
    }
    // checkDescription = (str) => {
    //     let message = 'Sorry, description for this character was not added yet';
    //     return !char.description ? 'Sorry, description for this character was not added yet' : char.description.length > 210 ? 
    //         `${char.description.slice(0, 211)}…` : char.description;
    // }

    _transformCharacter = (char) => {
        return {
            name: char.name,
            description: !char.description ? 'Sorry, description for this character was not added yet' : char.description.length > 210 ? 
            `${char.description.slice(0, 211)}…` : char.description,
            thumbnail: `${char.thumbnail.path}.${char.thumbnail.extension} `,
            homepage: char.urls[0].url,
            wiki: char.urls[1].url
        }
    }
}

export default MarvelService;
