import { Component } from 'react';

import MarvelService from '../../services/MarvelService';
import Spinner from '../spinner/Spinner';
import ErrorMessage from '../errorMessage/ErrorMessage';

import './charList.scss';

class CharList extends Component {

    constructor (props) {
        super(props)

        this.state = {
            charArr: [],
            loading: true,
            error: false,
            pages: 0
        }
    }

    marvelService = new MarvelService();

    componentDidMount() {
        this.updateCharList();
    }

    // componentDidUpdate(prevProps, prevState) {
    //     if (this.state.charArr.length !== prevProps.charArr.length) {
    //         this.updateChar();
    //         this.setState({loading: true, error: false});
    //     }
    // }

    onCharListLoaded = (newArray) => {
        this.setState(() => ({
            charArr: this.state.charArr.concat(newArray),
            loading: false,
            error: false
        }))
    }

    updateCharList = () => {
        this.marvelService
            .getAllCharacters()
            .then(charArr => this.onCharListLoaded(charArr))
            .catch(e => this.setState({loading: false, error: true}))
    }

    loadMore = () => {
        this.setState({
            loading: true,
            error: false,
            pages: this.state.pages + 1
        })
        this.marvelService
            .getMoreCharacters()
            .then(charArr => this.onCharListLoaded(charArr))
            .catch(e => this.setState({loading: false, error: true}))
    }
    renderCharListItems = (charArr) => {
        const items = charArr.map(item => {
            const {name, thumbnail, thumbnailCoverStatus, id} = item;
            return (
                <li className="char__item" onClick={() => this.props.onCharSelected(id)} key={id}>
                <img className={thumbnailCoverStatus} src={thumbnail} alt={name}/>
                    <div className="char__name">{name}</div>
                </li>
            )
        })

        return (
            <ul className="char__grid">
                {items}
            </ul>
        )
    }

    render = () => {
        const {charArr, loading, error, pages} = this.state;
        let statusLoading = loading && pages === 0 ? Spinner : null;
        let statusLoadMore = loading && pages > 0 ? Spinner : null;
        let statusError = error ? ErrorMessage : null;
        let currentStatus = statusError || statusLoading || this.renderCharListItems
        console.log(statusLoadMore, loading,  charArr.length, pages)
        return (
            <div className="char__list">
                {currentStatus(charArr)}
                {<statusLoadMore/>}
                <button onClick={this.loadMore} className="button button__main button__long">
                    <div className="inner">load more</div>
                </button>
            </div>
        )
    }
}

export default CharList;

