import { useEffect, useState } from 'react';

import useMarvelService from '../../services/MarvelService';
import Spinner from '../spinner/Spinner';
import ErrorMessage from '../errorMessage/ErrorMessage';

import './comicsList.scss';
import uw from '../../resources/img/UW.png';
import xMen from '../../resources/img/x-men.png';

const ComicsList = () => {

    const [charArr, setCharArr] = useState([]);
    const [pages, setPages] = useState(0);
    const [charEnded, setCharEnded] = useState(false);
    const [offset, setOffset] = useState(111);

    const {loading, error, getAllComics} = useMarvelService();

    useEffect(() => {
        onLoadingMore();
    }, []);

    const onComicsListLoaded = (newArr) => {

        setCharArr(charArr => {charArr.concat(newArr)});
    }

    const onLoadingMore = () => {
        setPages(pages => pages + 1);

        getAllComics(offset)
            .then(res => {onComicsListLoaded(res)})
    }

    const renderComicsItems = () => {
        const items = charArr.map(item => {
            const {title, prices, urls, thumbnail, id} = item;

            return (
                <li 
                    key={id}
                    className="comics__item"
                >
                    <a href={urls}>
                        <img src={thumbnail} alt={title} className="comics__item-img"/>
                        <div className="comics__item-name">{title}</div>
                        <div className="comics__item-price">{prices}</div>
                    </a>
                </li>
            )
        })

        return (
            <ul className="comics__grid">
                {items}
            </ul>
        )
    }

    const statusLoading = loading ? Spinner : null;
    const statusError = error ? ErrorMessage : null;
    const currentStatus = statusLoading || statusError || renderComicsItems;

    return (

        <div className="comics__list">
            {currentStatus()}
            <button 
                className="button button__main button__long"
            >
                <div className="inner">load more</div>
            </button>
        </div>
    )
}

export default ComicsList;

