import { useState } from "react";
import { useForm } from "react-hook-form"
import { Link } from "react-router-dom";

import useMarvelService from "../../services/MarvelService";

import './searchForm.scss';

const SearchForm = () => {

    const {getCharacterByName, error} = useMarvelService();

    const {register, handleSubmit, formState: {errors}, reset} = useForm();

    const [characterData, setCharacterData] = useState(null);

    const onSubmit = data => {
        // console.log(data.name)
        getCharacterByName(data.name)
            .then(resp =>{console.log(resp) ;setCharacterData(resp)})
            .catch(e=>{console.log(e)})
        reset();
    };
    // console.log(errors)

    const pageUrl = (data) => {
        return data.name;
    }

    // const checkOnValidName = (errors = null, data = null) => {
    //     // return characterData && Object.keys(characterData).length === 0 ? 
    //     // <Link 
    //     //     className="char__search_error"
    //     //     to={`../${pageUrl(characterData)}`}
    //     //     state={{from: characterData}}
    //     // >{renderURL(characterData.name)}</Link>
    //     if (errors.name) {(
    //         <p className="char__search_error">{errors.name.message}</p>
    //     )} else if (data && Object.keys(data).length === 0) {(
    //          <p className="char__search_error"></p>
    //     )} else {(
    //         <Link 
    //             className="char__search_found"
    //             to={`../${pageUrl(data.name)}`}
    //             state={{from: data}}
    //         >{renderURL(data.name)}</Link>
    //     )}
    //     }


    const renderURL = (name) => {
        return `There is! Visit ${name} page?`
    }

// console.log(characterData)

    return (
        <form onSubmit={handleSubmit(onSubmit)} className="char__search">
            <div className="char__search_label">Or find a character by name:</div>
            <input 
                {...register("name", 
                    {
                        required: 'This field is required',
                    }
                )} 
                type="text" 
                className="char__search_input"
            />
            <button 
                type="submit" 
                className="char__search_button button button__main"
            >
                <div className="inner">find</div>
            </button>
            {errors.name && <p className="char__search_error">{errors.name.message}</p>}
            {characterData && <Link 
                className="char__search_found"
                to={`../${pageUrl(characterData.name)}`}
                state={{from: characterData}}
            >{renderURL(characterData.name)}</Link>}
            {typeof characterData === 'string' && <p className="char__search_error">sorry</p>}
        </form>
    )
}
export default SearchForm;
